mapper.select:
select post.*, bo_name as po_bo_name from post join board on po_bo_num = bo_num where po_del = "N"
and po_bo_num = #{po_bo_num}
order by po_num desc

mapper.update:
update board 
set
	bo_name = #{board.bo_name}
where
	bo_num = #{board.bo_num}

update post 
set
	po_title = #{post.po_title},
	po_content = #{post.po_content}
where
	po_num = #{post.po_num}
